<script>
    import { onMount } from "svelte";
    import { auth } from '$lib/firebase/firebase.js';
    import { goto } from '$app/navigation';
    import Grid from "gridjs-svelte";
  
    let jsonData = [];
    let tableVisible = false;
    let isPopupVisible = false;

    let isAuthenticated = false; // Initialize a loading state

//  if the user is authenticated when the component mounts
onMount(async () => {
  // Check if the user is authenticated
if (auth.currentUser) {
  isAuthenticated = true; // User is authenticated
} else {
  // If the user is not authenticated, redirect them to the login page
  goto('/'); 
}
});
  
    onMount(async () => {
      const response = await fetch("https://api.recruitly.io/api/job?apiKey=TEST64518616D4CF145D4E20BD47169EA7229BA3");
      const responseData = await response.json();
      jsonData = responseData.data;
      tableVisible = true; // Show the table immediately
    });
  
    let isBlue = true;
  
    onMount(() => {
      // Create an interval to toggle the color every 2 seconds
      const interval = setInterval(() => {
        isBlue = !isBlue;
      }, 1000);
  
      // Clean up the interval when the component is unmounted
      return () => clearInterval(interval);
    });
  
    // Function to handle logout
    function handleLogout() {
  try {
// Remove user data from localStorage
localStorage.removeItem('user');
// Redirect to the login page
goto('/');
} catch (error) {
console.error("Error logging out:", error.message);
}
}
  
    function candidatelist() {
      goto('/candidatelist');
    }
  
    function joblist() {
      goto('/joblist');
    }
  
    function togglePopup() {
    isPopupVisible = !isPopupVisible;
  }

  function openBusinessProfile() {
    goto('/business');
    togglePopup(); // Close the popup after clicking "Business Profile"
  }
  </script>
  {#if isAuthenticated}
  <main>
    <div class="header">
      <div class="user-actions">
        <div class="user-profile"  >
      <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSuxByYdlAK5e7HyVnE_QyJ5RBP2Y8vlxuGjA&usqp=CAU" alt="User Profile" on:click={togglePopup} style="margin-left:0px;" >
    </div>
  </div>
      <img src="data:image/png;base64," alt="Your Logo" class="logo">
      <button class="candidatelist-button" on:click={candidatelist}>Candidates</button>
      <button class="joblist-button" on:click={joblist}>Jobs</button>
      <button class="logout-button" on:click={handleLogout}>Logout</button>
  
          
          {#if isPopupVisible}
          <div class="popup">
            <h1 class="popup-title">HireOptica</h1>
            <a class="popup-link" href="/business" on:click={openBusinessProfile}>Business Profile</a>
            <a class="popup-link" href="#" on:click={handleLogout}>Logout</a>
            <a class="popup-link" href="#" on:click={togglePopup}>Close</a>
          </div>
          <div class="popup-overlay" on:click={togglePopup}></div>
          {/if}
        </div>
    {#if tableVisible}
      <Grid
        search
        sort
        pagination={{ enabled: true, limit: 38 }}
        data={jsonData.map(item => ({
          title: item.title,
          jobType: item.jobType,
          status: item.status,
          experience: item.experience,
          industry: item.industry,
        }))} />
    {/if}
  </main>
{/if}
  
  <style global>
    @import "https://cdn.jsdelivr.net/npm/gridjs/dist/theme/mermaid.min.css";
  
    .h2 {
      margin-left: 650px;
      margin-top: 90px;
      font-weight: bold;
      font-size: 20px;
    }
  
    
.header {
    background-color: #333;
    color: white;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    padding: 10px 20px;
  }

  .logo {
    width: 100px;
    height: 30px; 
  }

  .header button {
    background-color: transparent;
    border: none;
    color: white;
    cursor: pointer;
    font-size: 16px;
    margin: 0 10px; /* Adjust the margins */
  }

  .header button:hover {
    text-decoration: underline;
  }

  .user-actions {
    display: flex;
    align-items: center;
  }

  .logout-button {
    background-color: red;
    padding: 10px 15px;
    border: none;
    cursor: pointer;
    color: white;
  }

  .user-profile img {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    cursor: pointer;
    margin-right: 1100px;
    justify-content: center;
  }

  .popup {
    position: absolute;
    top: 60px;
    left: 10px;
    background-color: white;
    border-radius: 5px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
    z-index: 999;
    width: 150px;
  }

  .popup-title {
    font-size: 20px;
    font-weight: bold;
    margin: 10px 0;
    text-align: center;
    color: darkred;
  }

  .popup-link {
    display: block;
    padding: 8px 0;
    text-decoration: none;
    color: black;
    text-align: center;
  }

  .popup-link:hover {
    background-color: #f0f0f0;
  }

  .popup-overlay {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.7);
    z-index: 998;
  }


  </style>
  